mapper.select:
select
  id, user_id, group_id, article_id, creat_time
from favorites_list


        and id = #{id}


        and user_id = #{userId}


        and group_id = #{groupId}


        and article_id = #{articleId}


        and creat_time = #{creatTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from favorites_list


        and id = #{id}


        and user_id = #{userId}


        and group_id = #{groupId}


        and article_id = #{articleId}


        and creat_time = #{creatTime}
